import numpy as np
from itertools import combinations_with_replacement

def num_to_digits(n):
    return list(map(int, str(n)))

def digits_to_num(digits):
    return int(''.join(map(str, digits)))

def is_good(n):
    expected_digit_sum = n % 10
    other_digits_sum = sum(num_to_digits(n // 10))

    return expected_digit_sum == other_digits_sum

def generate_numbers_with_n_digits(n):
    all_numbers_with_n_digits = []

    for combination in combinations_with_replacement(range(10), n):
        all_numbers_with_n_digits.extend(set(permutations(combination)))

    return [digits_to_num(digits) for digits in all_numbers_with_n_digits]

def compute_s(n):
    numbers_with_n_digits = generate_numbers_with_n_digits(n)
    good_numbers_with_n_digits = filter(is_good, numbers_with_n_digits)

    return np.sum(good_numbers_with_n_digits)

print(compute_s(2020))
```

